import React from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function PetCard({ photo, name, race, type, location }) {

    const [adopted, setAdopted] = React.useState(false)

    const flip = () => {
        setAdopted(!adopted)
    }

    return (
        <div>
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src={require(`./images/${photo}`)} />
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                    <Card.Text>{race}</Card.Text>
                    <Card.Text>{type}</Card.Text>
                    <Card.Text>{location}</Card.Text>
                    <Card.Text>{adopted ? 'Adopted' : 'Not adopted'}</Card.Text>
                    <Button variant="primary">Adopt Me </Button>
                </Card.Body>
            </Card>
        </div>
    )
}
