import React from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function PetCard({ photo, name, race, type, location }) {

    const [adopted, setAdopted] = React.useState(false)

    const flip = () => {
        setAdopted(!adopted)
    }

    return (
        <div>
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src={require(`./images/${photo}`)} />
                <Card.Body>
                    <Card.Title>name: {name}</Card.Title>
                    <Card.Text>Type: {race}</Card.Text>
                    <Card.Text>Race: {type}</Card.Text>
                    <Card.Text>Loaction: {location}</Card.Text>
                    {adopted ? <div style={{ color: 'green' }}>Adopted</div> : <div style={{ color: 'red' }}> Not Adopted</div>}
                    <Card.Text></Card.Text>
                    <Button onClick={flip} variant="primary">Adopt Me </Button>
                </Card.Body>
            </Card>
        </div >
    )
}
